vscode-custom.fileTemplate:
/**
 * Test suite for {{fileName}}
 * 
 * @module {{moduleName}}
 * @requires assert
 * @requires sinon
 * @vscode-custom This is a VS Code custom template
 */

import * as assert from 'assert';
import * as sinon from 'sinon';
import * as vscode from 'vscode';
{{imports}}

suite('{{suiteName}}', () => {
  // Stubs and mocks
  {{setupVars}}

  // Setup before each test
  setup(() => {
    {{setupCode}}
  });

  // Teardown after each test
  teardown(() => {
    // Restore all stubs
    sinon.restore();
  });

  {{tests}}
});

vscode-custom.testTemplate:
/**
 * {{testTag}}: Test that {{functionName}}() works correctly
 * 
 * @function {{functionName}}
 * @vscode-custom This is a VS Code custom test template
 */
test('{{functionName.camelCase}}() should work correctly', {{asyncPrefix}}() => {
  // Arrange
  {{mocks}}

  // Act
  {{actCode}}

  // Assert
  {{assertion}}

  {{#if returnType !== 'void'}}
  // Additional assertions for specific return type
  {{/if}}
});
